    @overload
    def title(self, _: list[str], **kwds) -> Theta2Datum: ...

    @overload
    def title(self, _: None, **kwds) -> Theta2Datum: ...

    @overload
    def type(
        self,
        _: Literal["quantitative", "ordinal", "temporal", "nominal", "geojson"],
        **kwds,
    ) -> Theta2Datum: ...

